package com.dianba.pos.menu.po;import javax.persistence.*;import java.io.Serializable;/** * Created by zhangyong on 2017/5/24. */@Entity@Table(name = "pos_passport_item", schema = "lift_pos")public class PosPassportItem implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ppi_id")    private Long ppiId;    @Column(name = "passport_id")    private Long passportId;    @Column(name = "item_id")    private Long itemId;    @Column(name = "create_date")    private String createDate;    @Column(name = "bug_count")    private Long buyCount;    @Column(name = "description")    private String description;    @Column(name = "is_shelve")    private String isShelve;    @Column(name = "is_delete")    private String isDelete;    @Column(name = "sales_price")    private Double salesPrice;    @Column(name = "repertory")    private Integer repertory;    @Column(name = "code_id")    private Integer codeId;    @Column(name = "menu_key")    private String menuKey;    public Long getPpiId() {        return ppiId;    }    public void setPpiId(Long ppiId) {        this.ppiId = ppiId;    }    public Long getPassportId() {        return passportId;    }    public void setPassportId(Long passportId) {        this.passportId = passportId;    }    public Long getItemId() {        return itemId;    }    public void setItemId(Long itemId) {        this.itemId = itemId;    }    public String getCreateDate() {        return createDate;    }    public Long getBuyCount() {        return buyCount;    }    public void setBuyCount(Long buyCount) {        this.buyCount = buyCount;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public String getIsShelve() {        return isShelve;    }    public void setIsShelve(String isShelve) {        this.isShelve = isShelve;    }    public String getIsDelete() {        return isDelete;    }    public void setIsDelete(String isDelete) {        this.isDelete = isDelete;    }    public Double getSalesPrice() {        return salesPrice;    }    public void setSalesPrice(Double salesPrice) {        this.salesPrice = salesPrice;    }    public Integer getRepertory() {        return repertory;    }    public void setRepertory(Integer repertory) {        this.repertory = repertory;    }    public Integer getCodeId() {        return codeId;    }    public void setCodeId(Integer codeId) {        this.codeId = codeId;    }    public String getMenuKey() {        return menuKey;    }    public void setMenuKey(String menuKey) {        this.menuKey = menuKey;    }}