package com.dianba.pos.passport.controller;import com.dianba.pos.common.util.AjaxJson;import com.dianba.pos.common.util.MapUtil;import com.dianba.pos.passport.api.PassportApi;import com.dianba.pos.passport.config.BaseUrlConstant;import com.dianba.pos.passport.util.PassportUtil;import com.dianba.pos.passport.vo.LoginPassportDto;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletResponse;import java.util.Map;/** * Created by zhangyong on 2017/5/22. */@Controller@RequestMapping(BaseUrlConstant.LOGIN_URL)public class PosLoginController {    @RequestMapping(value = "loginPassport")    public AjaxJson loginPassport(HttpServletResponse response, LoginPassportDto            loginPassportDto) {        AjaxJson ajaxJson = new AjaxJson();        Map<String, Object> map = MapUtil.beanToMap(loginPassportDto);        String result = PassportApi.posLogin(response, PassportUtil.POS_LOGIN_URL, map, "utf-8");        return ajaxJson;    }}